mapper.select:
SELECT content_id, content_type_id category, title, addr1, addr2, zipcode, tel, first_image, first_image2, readcount, sido_code, gugun_code, latitude, longitude, mlevel FROM attraction_info
content_type_id = #{category}
sido_code = #{sido}
gugun_code = #{gugun}
${condition.key} LIKE CONCAT('%', #{condition.word}, '%')
ORDER BY ${condition.orderBy} ${condition.orderByDir}
LIMIT #{condition.countPerPage} OFFSET #{condition.offset}
;
