mapper.insert:
		INSERT INTO 
			trip_comment (
				trip_comment_content,
				trip_comment_harmful, 
				trip_id, 
				user_id
			)
		VALUES ( 
			#{content}, 
			#{harmful},
			#{articleId}, 
			#{userId}
		)
		;

            select last_insert_id()
            ;

mapper.update:
UPDATE 
	trip_comment
SET 
	trip_comment_content = #{content},
	trip_comment_harmful = #{harmful}
WHERE 
	trip_comment_id = #{id}
;
