mapper.select:
SELECT t.trip_id, t.trip_name, t.user_id, t.trip_start_date, t.trip_end_date, t.trip_create_date, t.trip_content, u.user_name FROM trip t INNER JOIN user u ON t.user_id = u.user_id
${key} LIKE CONCAT('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}
LIMIT #{countPerPage} OFFSET #{offset}
;

mapper.insert:
