mapper.update:
UPDATE 
	trip
SET
	trip_name = #{tripName}, 
	trip_content = #{tripContent}, 
	trip_start_date = #{tripStartDate}, 
	trip_end_date = #{tripEndDate}
WHERE
	trip_id = #{tripId}	
;
